import AthleteCard  from "./AthleteCard"
import NewAthleteForm  from "./NewAthleteForm"
import UpdateFighter from "./UpdateFighter"

import { useState, useEffect } from 'react'

export default function Athletes() {
  const [fighters, setFighters] = useState([])
  const [openNewFighterForm, setOpenNewFighterForm] = useState(false)
  const [openUpdateFighterForm, setUpdateNewFighterForm] = useState(false)
  const [newFighter, setNewFighter] = useState({})

  // GET fighters
  useEffect(() => {
    const fetchFighters = async () => {
      let res = await fetch("/fighters");
      let fighterData = await res.json();
      setFighters(fighterData);
    };
      fetchFighters();
    },[]);

  // Add new fighter to list
  const onAddFighter = (newFighter) => {
    setFighters((fighters) => [...fighters, newFighter])
  }

  const onUpdateFighter = (updatedFighter) => {
    const updatedFighterArray = fighters.map((fighter) => {
      if (fighter.id === updatedFighter.id) {
        return updatedFighter;
      } else {
        return fighter;
      }
    })
    console.log(updatedFighter)
    console.log(updatedFighterArray)
    setFighters(updatedFighterArray)
  }

  return (
    <div className="bg-white">
      <div className="mx-auto max-w-2xl py-16 px-4 sm:py-24 sm:px-6 lg:max-w-7xl lg:px-8">
        <h2 className="text-2xl font-bold tracking-tight text-gray-900">All Athletes</h2>
        <div class="flex items-center gap-3 sm:gap-x-5">
    <button class="bg-white rounded-lg hover:bg-gray-100 duration-300 transition-colors border px-8 py-2.5">
        <svg class="w-5 h-5 sm:h-6 sm:w-6" viewBox="0 0 24 24" fill="none">
            Add New Athlete
            <clipPath id="clip0_3033_94664">
            <rect width="24" height="24" fill="white"/>
            </clipPath>
        </svg>
    </button>

    <button class="bg-white dark:bg-gray-900 dark:border-gray-700 dark:hover:bg-gray-800 rounded-lg hover:bg-gray-100 duration-300 transition-colors border px-8 py-2.5">
        <svg class="w-5 h-5 sm:h-6 sm:w-6" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
            <g clip-path="url(#clip0_3033_58407)">
            <path d="M24 12C24 5.37258 18.6274 0 12 0C5.37258 0 0 5.37258 0 12C0 17.9895 4.3882 22.954 10.125 23.8542V15.4688H7.07812V12H10.125V9.35625C10.125 6.34875 11.9166 4.6875 14.6576 4.6875C15.9701 4.6875 17.3438 4.92188 17.3438 4.92188V7.875H15.8306C14.34 7.875 13.875 8.80008 13.875 9.75V12H17.2031L16.6711 15.4688H13.875V23.8542C19.6118 22.954 24 17.9895 24 12Z" fill="#98A2B3"/>
