        <div className="hidden lg:flex lg:flex-1 lg:justify-end">
          <a className="text-sm font-semibold leading-6 text-gray-900" onClick={() => setOpenNewFighterForm(toggleForm)}>
            Add New Athlete <span aria-hidden="true">&rarr;</span>
          </a>
