// import { Dialog, Transition } from '@headlessui/react'
import { useState,useEffect, useRef } from 'react'

export default function NewAthleteForm({ newFighter, setNewFighter,onAddFighter }) {
    const [gyms, setGyms] = useState([])
    const [divisions, setDivisions] = useState([])
    const formRef = useRef(null);

    // GET GYMS to map in form
    useEffect(() => {
        const fetchGyms = async () => {
        let res = await fetch("http://localhost:3000/gyms?order=name");
        let gymData = await res.json();
        setGyms(gymData);
    };
      fetchGyms();
    },[]);

    // GET DIVISIONS to map in form - !!! Route not working !!!
    useEffect(() => {
    const fetchDivisions = async () => {
      let res = await fetch("http://localhost:3000/divisions");
      let divisionData = await res.json();
      setDivisions(divisionData);
    };
      fetchDivisions();
    },[]);

    // POST new fighter

    function handleSubmit(event) {
    event.preventDefault();

    const formData = new FormData(formRef.current);
    const addNewFighter = async () => {
        try {
        let res = await fetch('http://localhost:3000/fighters', {
            method: 'POST',
            body: formData
        })
        let newFighterData = await res.json();
        setNewFighter(newFighterData); // Log the server's response to the console
        } catch (error) {
        console.error(error); // Log any errors to the console
        // Do something to handle the error, such as displaying an error message to the user
        }
    }
    }

    return (
        <form ref={formRef} onSubmit={handleSubmit}>
        <label>
            First Name:
            <input type="text" name="first_name" />
        </label>
        <label>
        Last Name:
        <input type="text" name="last_name" />
      </label>
      <label>
        Nickname:
        <input type="text" name="nickname" />
      </label>
      <label>
        Email:
        <input type="text" name="email" />
      </label>
      <label>
        Gender:
        <input type="text" name="gender" />
      </label>
      <label>
        Gym ID:
        <input type="number" name="gym_id" />
      </label>
      <label>
        Division ID:
        <input type="number" name="division_id" />
      </label>
      <label>
        Wins:
        <input type="number" name="wins" />
      </label>
      <label>
        Losses:
        <input type="number" name="losses" />
      </label>
      <label>
        Draw:
        <input type="number" name="draw" />
      </label>
      <label>
        Headshot:
        <input type="text" name="headshot" />
      </label>
      <label>
        Instagram:
        <input type="text" name="insta" />
      </label>
