import { useState, useEffect } from 'react'

export default function UpdateFighter({ 
    fighters, 
    onUpdateFighter
    // updateFighter, 
    // setUpdateFighter,
    }) {

    const [gyms, setGyms] = useState([])
    const [divisions, setDivisions] = useState([])
    const [selectedFighter, setSelectedFighter] = useState(fighters[0]);

    const [updatedFighter, setUpdatedFighter] = useState({
        first_name: "",
        last_name: "",
        nickname: "",
        email: "",
        gender: "",
        gym_id: "",
        division_id: "",
        wins: "",
        losses: "",
        draw: "",
        headshot: "",
        insta: "",
        status: ""
    });

    // const [fighterData, setFighterData] = useState({
    //     firstName: selectedFighter.first_name,
    //     lastName: selectedFighter.last_name,
    //     nickname: selectedFighter.nickname,
    //     email: selectedFighter.email,
    //     gender: selectedFighter.gender,
    //     gymId: selectedFighter.gym_id,
    //     divisionId: selectedFighter.division_id,
    //     wins: selectedFighter.wins,
    //     losses: selectedFighter.losses,
    //     draw: selectedFighter.draw,
    //     headshot: selectedFighter.headshot,
    //     insta: selectedFighter.insta,
    //     status: selectedFighter.status,
    // });

  //  GET GYMS to map in form

  useEffect(() => {
      const fetchGyms = async () => {
      let res = await fetch("http://localhost:3000/gyms");
      let gymData = await res.json();
      setGyms(gymData);
  };
    fetchGyms();
  },[]);

  //  GET DIVISIONS to map in form 
  useEffect(() => {
  const fetchDivisions = async () => {
    let res = await fetch("http://localhost:3000/divisions");
    let divisionData = await res.json();
    setDivisions(divisionData);
  };
    fetchDivisions();
  },[]);

//   const handleChange = (event) => {
//     const { name, value } = event.target;
//     setFighterData({ ...fighterData, [name]: value });
//   };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await fetch(`/fighters/${selectedFighter}`, {
    method: 'PATCH',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({
        updatedFighter
    }),
  })
    .then((response) => {
      if (!response.ok) {
        throw new Error('Failed to update fighter');
      }
      return response.json();
    })
    .then((updatedData) => {
      // handle success
    //   console.log(updatedData)
    onUpdateFighter(updatedData)
    })
    .catch((error) => {
      // handle error
      console.log('error')
    })
  }


  return (
    <div>
        <div className="col-span-6 sm:col-span-3">
            <label className="block text-sm font-medium leading-6 text-gray-900">
                Pick an Athlete to Update
            </label>
            <select
                onChange={(e)=>
                    setSelectedFighter(e.target.value)}
                id="gym_id"
                name="gym_id"
                className="mt-2 block w-full rounded-md border-0 bg-white py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
                >
                    <option></option>
                {fighters.map(fighter => (
                    <option key={fighter.id} value={fighter.id}>{fighter.first_name} {fighter.last_name} - {fighter.gym_id}</option>
                    ))}
            </select>
        </div>
      <form method="PATCH" onSubmit={handleSubmit}>
        <div className="grid grid-cols-6 gap-6">
            <div className="col-span-6 sm:col-span-3">
                <label htmlFor="first-name" className="block text-sm font-medium leading-6 text-gray-900">
                First name
                </label>
                <input
                type="text"
                name="first_name"
                value={updatedFighter.first_name}
                placeholder="first name"
                className="mt-2 block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
            />
            </div>

            <div className="col-span-6 sm:col-span-3">
            <label htmlFor="last-name" className="block text-sm font-medium leading-6 text-gray-900">
                Last name
            </label>
            <input
                type="text"
                name="last_name"
                id="last_name"

                className="mt-2 block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
            />
            </div>

            <div className="col-span-6 sm:col-span-4">
            <label htmlFor="email-address" className="block text-sm font-medium leading-6 text-gray-900">
                Email address
            </label>
            <input
                type="text"
                name="email"
                id="email"

                className="mt-2 block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
            />
            </div>

            <div className="col-span-6 sm:col-span-1">
            <label className="block text-sm font-medium leading-6 text-gray-900">
                Gender
            </label>
            <select
                id="gender"
                name="gender"

                className="mt-2 block w-full rounded-md border-0 bg-white py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
            >
                <option></option>
                <option>M</option>
                <option>F</option>
            </select>
            </div>

            <div className="col-span-6 sm:col-span-3">
            <label className="block text-sm font-medium leading-6 text-gray-900">
                Gym
            </label>
            <select
                id="gym_id"
                name="gym_id"
                className="mt-2 block w-full rounded-md border-0 bg-white py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
                >
                <option></option>
                {gyms.map(gym => (
                    <option key={gym.id} value={gym.id}>{gym.name}</option>
                    ))}
            </select>
            </div>

            <div className="col-span-6 sm:col-span-3">
            <label className="block text-sm font-medium leading-6 text-gray-900">
                Division
            </label>
            <select
                id="division_id"
                name="division_id"
                className="mt-2 block w-full rounded-md border-0 bg-white py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
                >
                <option></option>
                {divisions.map((divison) => (
                <option key={divison.id} value={divison.id}>
                    {divison.name} - {divison.lbs}lbs ({divison.kg}kg)
                    </option>
                ))}
            </select>
            </div>

            <div className="col-span-6 sm:col-span-1">
            <label className="block text-sm font-medium leading-6 text-gray-900">
                Wins
            </label>
            <input
                type="number"
                name="wins"
                id="wins"

                className="mt-2 block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
            />
            </div>

            <div className="col-span-6 sm:col-span-1">
            <label className="block text-sm font-medium leading-6 text-gray-900">
                Losses
            </label>
