import FightCard from './FightCard'
export default function EventDetailPage({event}) {
return (
    <>
    <FightCard/>
    {event.map((event)=> {
        return(
            <div id="event-info">
            <img id="event-image" src={event.poster_img} />
            <div>
                <h1 id="event-name">#{event.event_number} {event.name}</h1>
                <h3 id="event-details">{event.date} - {event.location}</h3>
                <p id="description">
                    Description
                    {event.fights.map((fight) => {
                        return(
                            <>
                            <h3>fight matchup</h3>
                            <h4></h4>
                            <span>athlete 1: {fight.fighter1.first_name} {fight.fighter1.last_name}</span><br/>
                            <span>athlete 2: {fight.fighter2.first_name} {fight.fighter2.last_name}</span>
                            </>
                        )
                    })}
                </p>
            </div>
        </div>
        )
    })}
    </>
    )
}
